import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import PaymentForm from './PaymentForm'; // Importar el formulario de pago
import './ProductList.css'; // Crear y usar un archivo CSS para los estilos específicos

const ProductList = () => {
  const [productos, setProductos] = useState([]); // Lista de productos
  const [selectedProduct, setSelectedProduct] = useState(null); // Producto seleccionado
  const [loading, setLoading] = useState(true); // Indicador de carga
  const [error, setError] = useState(null); // Manejo de errores

  // Obtener la lista de productos desde el backend
  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = await axios.get('http://localhost:3000/productos');
        setProductos(response.data);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError('Error al cargar los productos. Inténtelo de nuevo.');
      } finally {
        setLoading(false); // Detener la carga independientemente del éxito o fallo
      }
    };

    fetchProductos();
  }, []);

  // Abrir modal con el producto seleccionado
  const openModal = (producto) => {
    setSelectedProduct(producto);
  };

  // Cerrar el modal
  const closeModal = () => {
    setSelectedProduct(null); // Devolver el valor a null cuando se cierra
  };

  if (loading) {
    return <p>Cargando productos...</p>; // Spinner o mensaje de carga
  }

  if (error) {
    return <p>{error}</p>; // Mostrar error si la carga falla
  }

  return (
    <div className="product-list">
      <h2>Productos Disponibles</h2>
      <ul>
        {productos.map(({ id, nombre, descripcion, precio, stock }) => (
          <li key={id}>
            <h3>{nombre}</h3>
            <p>{descripcion}</p>
            <p>Precio: {precio.toLocaleString()} COP</p>
            <p>Stock: {stock}</p>
            <button onClick={() => openModal({ id, nombre, precio })}>
              Comprar
            </button>
          </li>
        ))}
      </ul>

      {/* Modal para el formulario de pago */}
      {selectedProduct && (
        <Modal isOpen={!!selectedProduct} onRequestClose={closeModal}>
          <h2>Formulario de Pago</h2>
          <PaymentForm producto={selectedProduct} onClose={closeModal} />
        </Modal>
      )}
    </div>
  );
};

export default ProductList;
