import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal";

// Función para validar el número de la tarjeta con el algoritmo Luhn
const validateCardNumber = (cardNumber) => {
  let sum = 0;
  let shouldDouble = false;

  for (let i = cardNumber.length - 1; i >= 0; i--) {
    let digit = parseInt(cardNumber[i]);

    if (shouldDouble) {
      digit *= 2;
      if (digit > 9) digit -= 9;
    }

    sum += digit;
    shouldDouble = !shouldDouble;
  }

  return sum % 10 === 0;
};

// Detectar si la tarjeta es VISA o MasterCard
const getCardType = (cardNumber) => {
  if (/^4/.test(cardNumber)) return "VISA";
  if (/^5[1-5]/.test(cardNumber)) return "MasterCard";
  return "Unknown";
};

function ProductList() {
  const [products, setProducts] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cardNumber, setCardNumber] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [cvc, setCvc] = useState("");
  const [errors, setErrors] = useState({});
  const [cardType, setCardType] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(""); // Estado para mostrar feedback del pago

  useEffect(() => {
    // Llamada al backend para obtener los productos
    axios
      .get("http://localhost:3000/productos")
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
      });
  }, []);

  const handlePayment = (product) => {
    setSelectedProduct(product);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  // Función para manejar la validación del pago
  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = {};

    // Validar número de tarjeta
    if (!validateCardNumber(cardNumber)) {
      newErrors.cardNumber = "Número de tarjeta inválido";
    } else {
      setCardType(getCardType(cardNumber));
    }

    // Validar fecha de expiración (Formato MM/YY)
    if (!/^(0[1-9]|1[0-2])\/?([0-9]{2})$/.test(expiryDate)) {
      newErrors.expiryDate = "Fecha de expiración inválida";
    }

    // Validar CVC
    if (!/^[0-9]{3}$/.test(cvc)) {
      newErrors.cvc = "CVC inválido";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Si no hay errores, procesamos el pago
      const payload = {
        productoId: selectedProduct.id,
        monto: selectedProduct.precio,
        token: "tu-token-generado", // Aquí debes pasar el token generado en el backend
        acceptanceToken: "tu-acceptance-token", // Aquí debes pasar el acceptance token
        customerEmail: "cliente@example.com",
      };

      console.log("Payload enviado:", payload); // Revisa el payload antes de enviarlo

      try {
        const createResponse = await axios.post(
          "http://localhost:3000/transacciones/crear",
          payload
        );

        console.log("Transacción creada exitosamente:", createResponse.data);

        // Aquí hacemos la segunda solicitud para completar la transacción
        const completeResponse = await axios.post(
          "http://localhost:3000/transacciones/completar",
          {
            transaccionId: createResponse.data.id,
            estado: "COMPLETADO", // Marcamos como completado
          }
        );

        console.log("Transacción completada:", completeResponse.data);

        // Actualizamos el stock del producto en la lista de productos
        const updatedProducts = products.map((product) =>
          product.id === selectedProduct.id
            ? { ...product, stock: product.stock - 1 } // Restamos 1 al stock
            : product
        );

        setProducts(updatedProducts); // Actualizamos el estado con los productos actualizados

        // Mostrar mensaje de éxito
        setPaymentStatus("Pago procesado exitosamente.");
        closeModal();
      } catch (error) {
        console.error(
          "Error al procesar el pago:",
          error.response ? error.response.data : error.message
        );
        setPaymentStatus("Error al procesar el pago.");
      }
    }
  };

  return (
    <div>
      <h1>Productos Disponibles</h1>
      {paymentStatus && <p>{paymentStatus}</p>} {/* Mostrar mensaje de feedback */}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h2>{product.nombre}</h2>
            <p>{product.descripcion}</p>
            <p>Precio: ${product.precio}</p>
            <p>Stock: {product.stock}</p>
            <button onClick={() => handlePayment(product)}>
              Pagar con tarjeta de crédito
            </button>
          </li>
        ))}
      </ul>

      {/* Modal de pago */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Pago con tarjeta"
      >
        {selectedProduct && (
          <div>
            <h2>Pagar {selectedProduct.nombre}</h2>
            <form onSubmit={handleSubmit}>
              <div>
                <label>Número de Tarjeta</label>
                <input
                  type="text"
                  placeholder="0000 0000 0000 0000"
                  value={cardNumber}
                  onChange={(e) => setCardNumber(e.target.value)}
                />
                {errors.cardNumber && (
                  <p style={{ color: "red" }}>{errors.cardNumber}</p>
                )}
                {cardType && <p>Tipo de tarjeta: {cardType}</p>}
              </div>
              <div>
                <label>Fecha de Expiración (MM/YY)</label>
                <input
                  type="text"
                  placeholder="MM/YY"
                  value={expiryDate}
                  onChange={(e) => setExpiryDate(e.target.value)}
                />
                {errors.expiryDate && (
                  <p style={{ color: "red" }}>{errors.expiryDate}</p>
                )}
              </div>
              <div>
                <label>CVC</label>
                <input
                  type="text"
                  placeholder="CVC"
                  value={cvc}
                  onChange={(e) => setCvc(e.target.value)}
                />
                {errors.cvc && <p style={{ color: "red" }}>{errors.cvc}</p>}
              </div>
              <button type="submit">Realizar Pago</button>
              <button type="button" onClick={closeModal}>
                Cancelar
              </button>
            </form>
          </div>
        )}
      </Modal>
    </div>
  );
}

export default ProductList;
