import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ producto, onClose }) => {
  const [numeroTarjeta, setNumeroTarjeta] = useState('');
  const [cvc, setCvc] = useState('');
  const [expMonth, setExpMonth] = useState('');
  const [expYear, setExpYear] = useState('');
  const [cardHolder, setCardHolder] = useState(''); // Estado para el nombre del titular de la tarjeta
  const [email, setEmail] = useState(''); // Estado para el correo electrónico
  const [mensaje, setMensaje] = useState('');

  // Función para obtener el acceptanceToken
  const obtenerAcceptanceToken = async () => {
    try {
      const response = await axios.get(
        'https://api-sandbox.co.uat.wompi.dev/v1/merchants/pub_stagtest_g2u0HQd3ZMh05hsSgTS2lUV8t3s4mOt7'
      );
      const acceptanceToken = response.data.data.presigned_acceptance.acceptance_token;
      console.log('Acceptance Token:', acceptanceToken);
      return acceptanceToken;
    } catch (error) {
      console.error('Error obteniendo el acceptance token:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Obtener el token de la tarjeta
      const tokenResponse = await axios.post(
        'https://api-sandbox.co.uat.wompi.dev/v1/tokens/cards',
        {
          number: numeroTarjeta,
          cvc,
          exp_month: expMonth,
          exp_year: expYear,
          card_holder: cardHolder, // El valor del titular de la tarjeta
        },
        {
          headers: {
            Authorization: `Bearer pub_stagtest_g2u0HQd3ZMh05hsSgTS2lUV8t3s4mOt7`, // Clave pública de Wompi
          },
        }
      );

      const token = tokenResponse.data.data.id; // Obtiene el token de la respuesta de Wompi
      console.log('Token generado:', token);

      // Obtener el acceptanceToken dinámicamente
      const acceptanceToken = await obtenerAcceptanceToken();

      // Luego enviamos el token junto con los datos al backend
      const transaccionData = {
        productoId: producto.id,
        monto: producto.precio,
        token, // Incluimos el token generado
        acceptanceToken, // Incluimos el acceptance token generado dinámicamente
        payment_method: {
          type: 'CARD',
          token: token, // El token generado
          installments: 1, // El número de cuotas es 1
        },
        reference: `transaccion_ref_${producto.id}`,
        customer_email: email, // Ahora correctamente se envía el correo electrónico
        cardHolder: cardHolder, // Nombre del titular de la tarjeta, ahora correctamente enviado
      };

      // Verifica que el correo electrónico está en el objeto correctamente
      console.log('Datos de transacción enviados:', transaccionData);

      const response = await axios.post('http://localhost:3000/transacciones/crear', transaccionData);
      setMensaje(`Transacción ${response.data.estado}: ¡Gracias por tu compra!`);
    } catch (error) {
      // Verifica si el error proviene de la respuesta del servidor
      if (error.response) {
        console.error('Error del servidor:', error.response.data);
        setMensaje('Error al procesar la transacción: ' + error.response.data.message);
      } else {
        console.error('Error:', error.message);
        setMensaje('Error al procesar la transacción');
      }
    }
  };

  return (
    <div>
      <h3>Comprar {producto.nombre}</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre del titular:</label> {/* Campo para el nombre del titular */}
          <input
            type="text"
            value={cardHolder}
            onChange={(e) => setCardHolder(e.target.value)}
            placeholder="Juan Perez"
            required
          />
        </div>
        <div>
          <label>Número de tarjeta:</label>
          <input
            type="text"
            value={numeroTarjeta}
            onChange={(e) => setNumeroTarjeta(e.target.value)}
            placeholder="4111 1111 1111 1111"
            required
          />
        </div>
        <div>
          <label>CVC:</label>
          <input
            type="text"
            value={cvc}
            onChange={(e) => setCvc(e.target.value)}
            placeholder="123"
            required
          />
        </div>
        <div>
          <label>Mes de expiración:</label>
          <input
            type="text"
            value={expMonth}
            onChange={(e) => setExpMonth(e.target.value)}
            placeholder="12"
            required
          />
        </div>
        <div>
          <label>Año de expiración:</label>
          <input
            type="text"
            value={expYear}
            onChange={(e) => setExpYear(e.target.value)}
            placeholder="2025"
            required
          />
        </div>
        <div>
          <label>Correo electrónico:</label> {/* Campo para el correo electrónico */}
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="prueba@gmail.com"
            required
          />
        </div>
        <button type="submit">Pagar</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default PaymentForm;
