import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal";

// Función para validar el número de la tarjeta con el algoritmo Luhn
const validateCardNumber = (cardNumber) => {
    let sum = 0;
    let shouldDouble = false;
  
    for (let i = cardNumber.length - 1; i >= 0; i--) {
      let digit = parseInt(cardNumber[i]);
  
      if (shouldDouble) {
        digit *= 2;
        if (digit > 9) digit -= 9;
      }
  
      sum += digit;
      shouldDouble = !shouldDouble;
    }
  
    return sum % 10 === 0;
  };
  
  // Detectar si la tarjeta es VISA o MasterCard
  const getCardType = (cardNumber) => {
    if (/^4/.test(cardNumber)) return "VISA";
    if (/^5[1-5]/.test(cardNumber)) return "MasterCard";
    return "Unknown";
  };

function ProductList () {
  const [products, setProducts] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    // Llamada al backend para obtener los productos
    axios
      .get("http://localhost:3000/productos")
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
      });
  }, []);

  const handlePayment = (product) => {
    setSelectedProduct(product);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div>
      <h1>Productos Disponibles</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h2>{product.nombre}</h2>
            <p>{product.descripcion}</p>
            <p>Precio: ${product.precio}</p>
            <p>Stock: {product.stock}</p>
            <button onClick={() => handlePayment(product)}>
              Pagar con tarjeta de crédito
            </button>
          </li>
        ))}
      </ul>

      {/* Modal de pago*/}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Pago con tarjeta"
      >
        {selectedProduct && (
          <div>
            <h2>Pagar{selectedProduct.nombre}</h2>
            <form>
              <div>
                <label>Número de Tarjeta</label>
                <input type="text" placeholder="0000 0000 0000 0000" />
              </div>
              <div>
                <label>Fecha de Expiración</label>
                <input type="text" placeholder="MM/YY" />
              </div>
              <div>
                <label>CVC:</label>
                <input type="text" placeholder="CVC" />
              </div>
              <button type="submit">Realizar Pago</button>
              <button type="button" onClick={closeModal}>
                Cancelar
              </button>
            </form>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default ProductList;
