import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import PaymentForm from './PaymentForm'; // Importar el formulario de pago

const ProductList = () => {
  const [productos, setProductos] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // Producto seleccionado para compra
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Obtener la lista de productos desde el backend
  useEffect(() => {
    axios.get('http://localhost:3000/productos')
      .then(response => setProductos(response.data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  // Abrir modal con el producto seleccionado
  const openModal = (producto) => {
    setSelectedProduct(producto);
    setModalIsOpen(true);
  };

  // Cerrar el modal
  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div>
      <h2>Productos Disponibles</h2>
      <ul>
        {productos.map((producto) => (
          <li key={producto.id}>
            <h3>{producto.nombre}</h3>
            <p>{producto.descripcion}</p>
            <p>Precio: {producto.precio} COP</p>
            <p>Stock: {producto.stock}</p>
            <button onClick={() => openModal(producto)}>Comprar</button>
          </li>
        ))}
      </ul>

      {/* Modal para el formulario de pago */}
      {selectedProduct && (
        <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
          <h2>Formulario de Pago</h2>
          <PaymentForm producto={selectedProduct} onClose={closeModal} />
        </Modal>
      )}
    </div>
  );
};

export default ProductList;
