import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ producto, onClose }) => {
  const [numeroTarjeta, setNumeroTarjeta] = useState('');
  const [cvc, setCvc] = useState('');
  const [expMonth, setExpMonth] = useState('');
  const [expYear, setExpYear] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const transaccionData = {
      productoId: producto.id,
      monto: producto.precio,
      numeroTarjeta, // Pasar los datos de la tarjeta
      cvc,
      expMonth,
      expYear
    };

    console.log('Datos de la transacción:', transaccionData); // Verifica qué datos se están enviando
  
    try {
      const response = await axios.post('http://localhost:3000/transacciones/crear', transaccionData);
      setMensaje(`Transacción ${response.data.estado}: ¡Gracias por tu compra!`);
    } catch (error) {
      if (error.response) {
        console.error('Error del servidor:', error.response.data);
        setMensaje('Error al procesar la transacción: ' + error.response.data.message);
      } else {
        console.error('Error:', error.message);
        setMensaje('Error al procesar la transacción');
      }
    }
  };

  return (
    <div>
      <h3>Comprar {producto.nombre}</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Número de tarjeta:</label>
          <input
            type="text"
            value={numeroTarjeta}
            onChange={(e) => setNumeroTarjeta(e.target.value)}
            placeholder="4111 1111 1111 1111"
            required
          />
        </div>
        <div>
          <label>CVC:</label>
          <input
            type="text"
            value={cvc}
            onChange={(e) => setCvc(e.target.value)}
            placeholder="123"
            required
          />
        </div>
        <div>
          <label>Mes de expiración:</label>
          <input
            type="text"
            value={expMonth}
            onChange={(e) => setExpMonth(e.target.value)}
            placeholder="12"
            required
          />
        </div>
        <div>
          <label>Año de expiración:</label>
          <input
            type="text"
            value={expYear}
            onChange={(e) => setExpYear(e.target.value)}
            placeholder="2025"
            required
          />
        </div>
        <button type="submit">Pagar</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default PaymentForm;
