import React, { useState } from 'react';
import axios from 'axios';
import './PaymentForm.css'; // Estilos separados

const PaymentForm = ({ producto, onClose }) => {
  const [numeroTarjeta, setNumeroTarjeta] = useState('');
  const [cvc, setCvc] = useState('');
  const [expMonth, setExpMonth] = useState('');
  const [expYear, setExpYear] = useState('');
  const [cardHolder, setCardHolder] = useState('');
  const [customerEmail, setCustomerEmail] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [loading, setLoading] = useState(false); // Indicador de carga

  const obtenerAcceptanceToken = async () => {
    try {
      const response = await axios.get(
        'https://api-sandbox.co.uat.wompi.dev/v1/merchants/pub_stagtest_g2u0HQd3ZMh05hsSgTS2lUV8t3s4mOt7'
      );
      return response.data.data.presigned_acceptance.acceptance_token;
    } catch (error) {
      console.error('Error obteniendo el acceptance token:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Iniciar el indicador de carga

    try {
      const tokenResponse = await axios.post(
        'https://api-sandbox.co.uat.wompi.dev/v1/tokens/cards',
        {
          number: numeroTarjeta,
          cvc,
          exp_month: expMonth,
          exp_year: expYear,
          card_holder: cardHolder,
        },
        {
          headers: {
            Authorization: `Bearer pub_stagtest_g2u0HQd3ZMh05hsSgTS2lUV8t3s4mOt7`,
          },
        }
      );

      const token = tokenResponse.data.data.id;
      const acceptanceToken = await obtenerAcceptanceToken();

      const transaccionData = {
        productoId: producto.id,
        monto: producto.precio,
        token,
        acceptanceToken,
        payment_method: {
          type: 'CARD',
          token: token,
          installments: 1,
        },
        reference: `transaccion_ref_${producto.id}`,
        customerEmail,
        cardHolder,
      };

      const response = await axios.post('http://localhost:3000/transacciones/crear', transaccionData);
      setMensaje(`Transacción ${response.data.estado}: ¡Gracias por tu compra!`);
    } catch (error) {
      if (error.response) {
        console.error('Error del servidor:', error.response.data);
        setMensaje('Error al procesar la transacción: ' + JSON.stringify(error.response.data.message));
      } else {
        console.error('Error:', error.message);
        setMensaje('Error al procesar la transacción');
      }
    } finally {
      setLoading(false); // Detener el indicador de carga
    }
  };

  return (
    <div className="payment-form">
      <h3>Comprar {producto.nombre}</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre del titular:</label>
          <input
            type="text"
            value={cardHolder}
            onChange={(e) => setCardHolder(e.target.value)}
            placeholder="Juan Perez"
            required
          />
        </div>
        <div className="form-group">
          <label>Número de tarjeta:</label>
          <input
            type="text"
            value={numeroTarjeta}
            onChange={(e) => setNumeroTarjeta(e.target.value)}
            placeholder="4111 1111 1111 1111"
            required
          />
        </div>
        <div className="form-group">
          <label>CVC:</label>
          <input
            type="text"
            value={cvc}
            onChange={(e) => setCvc(e.target.value)}
            placeholder="123"
            required
          />
        </div>
        <div className="form-group">
          <label>Mes de expiración:</label>
          <input
            type="text"
            value={expMonth}
            onChange={(e) => setExpMonth(e.target.value)}
            placeholder="12"
            required
          />
        </div>
        <div className="form-group">
          <label>Año de expiración:</label>
          <input
            type="text"
            value={expYear}
            onChange={(e) => setExpYear(e.target.value)}
            placeholder="2025"
            required
          />
        </div>
        <div className="form-group">
          <label>Correo electrónico:</label>
          <input
            type="email"
            value={customerEmail}
            onChange={(e) => setCustomerEmail(e.target.value)}
            placeholder="email@ejemplo.com"
            required
          />
        </div>

        {loading ? (
          <div className="loading">Procesando...</div>
        ) : (
          <button type="submit">Pagar</button>
        )}
      </form>
      {mensaje && <p>{mensaje}</p>}
      <button onClick={onClose}>Cerrar</button>
    </div>
  );
};

export default PaymentForm;
