import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux'; // Usamos hooks de Redux
import Modal from 'react-modal';
import PaymentForm from './PaymentForm'; // Importar el formulario de pago
import { fetchProducts } from '../features/productSlice'; // Acción de Redux
import './ProductList.css'; // Asegúrate de haber vinculado el archivo CSS

const ProductList = () => {
  const dispatch = useDispatch();
  const { products, status, error } = useSelector((state) => state.product); // Conectamos con el store de productos
  const [selectedProduct, setSelectedProduct] = React.useState(null); // Producto seleccionado para compra
  const [modalIsOpen, setModalIsOpen] = React.useState(false);

  // Obtener productos desde Redux
  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchProducts());
    }
  }, [status, dispatch]);

  // Abrir modal con el producto seleccionado
  const openModal = (producto) => {
    setSelectedProduct(producto);
    setModalIsOpen(true);
  };

  // Cerrar el modal
  const closeModal = () => {
    setSelectedProduct(null);
    setModalIsOpen(false);
  };

  if (status === 'loading') {
    return <div>Cargando productos...</div>;
  }

  if (status === 'failed') {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="product-list">
      <h2>Productos Disponibles</h2>
      <ul>
        {products.map(({ id, nombre, descripcion, precio, stock }) => (
          <li key={id}>
            <h3>{nombre}</h3>
            <p>{descripcion}</p>
            <p>Precio: {precio.toLocaleString()} COP</p>
            <p>Stock: {stock}</p>
            <button onClick={() => openModal({ id, nombre, precio })}>Comprar</button>
          </li>
        ))}
      </ul>

      {selectedProduct && (
        <Modal 
          isOpen={modalIsOpen} 
          onRequestClose={closeModal} 
          className="react-modal-content" 
          overlayClassName="react-modal-overlay"
          ariaHideApp={false}  // Evitar problemas en desarrollo con el aria
        >
          <PaymentForm producto={selectedProduct} onClose={closeModal} />
        </Modal>
      )}
    </div>
  );
};

export default ProductList;
