import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Llamada al backend para obtener los productos
    axios.get('http://localhost:3000/productos')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  }, []);

  const handlePayment = (producto)=>{
    setSelectedProduct(producto);
    setModalIsOpen(true);

  };

  const closeModal = () => {
    setModalIsOpen(false);
  };



  return (
    <div>
      <h1>Productos Disponibles</h1>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            <h2>{product.nombre}</h2>
            <p>{product.descripcion}</p>
            <p>Precio: ${product.precio}</p>
            <p>Stock: {product.stock}</p>
            <button onClick={() => handlePayment(producto)}>Pagar con tarjeta de crédito</button>
          </li>
        ))}
      </ul>

      {/* Modal de pago*/}
      <Modal 
      isOpen = {modalIsOpen}
      onRequestClose={closeModal}
      contentLabel="Pago con tarjeta"
      >
        {selectedProduct &&(
            <div>
                <h2>Pagar{selectedProduct.nombre}</h2>
                <form>
                    <div>
                    <label>Número de Tarjeta</label>
                    <input type='text' placeholder='0000 0000 0000 0000' />
                    </div>
                    <div>
                    <label>Fecha de Expiración</label>
                    <input type='text' placeholder='MM/YY' />
                    </div>
                    <div>
                    <label>CVC:</label>
                    <input type='text' placeholder='CVC' />
                    </div>
                    <button type='submit'>Realizar Pago</button>
                    <button type='button' onClick={closeModal}>Cancelar</button>
                </form>
            </div>
        )}
      </Modal>
    </div>
  );
};

export default ProductList;
