import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import PaymentForm from './PaymentForm'; // Importar el formulario de pago
import './ProductList.css'; // Asegúrate de haber vinculado el archivo CSS

const ProductList = () => {
  const [productos, setProductos] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // Producto seleccionado para compra
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Obtener la lista de productos desde el backend
  useEffect(() => {
    axios.get('http://localhost:3000/productos')
      .then(response => setProductos(response.data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  // Abrir modal con el producto seleccionado
  const openModal = (producto) => {
    setSelectedProduct(producto);
    setModalIsOpen(true);
  };

  // Cerrar el modal
  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="product-list">
      <h2>Productos Disponibles</h2>
      <ul>
        {productos.map(({ id, nombre, descripcion, precio, stock }) => (
          <li key={id}>
            <h3>{nombre}</h3>
            <p>{descripcion}</p>
            <p>Precio: {precio.toLocaleString()} COP</p>
            <p>Stock: {stock}</p>
            <button onClick={() => openModal({ id, nombre, precio })}>Comprar</button>
          </li>
        ))}
      </ul>

      {/* Modal para el formulario de pago */}
      {selectedProduct && (
        <Modal 
          isOpen={modalIsOpen} 
          onRequestClose={closeModal} 
          className="react-modal-content"
          overlayClassName="react-modal-overlay"
          ariaHideApp={false}  // Esto evita problemas en desarrollo con el aria
        >
          {/* <h2>Formulario de Pago</h2> */}
          <PaymentForm producto={selectedProduct} onClose={closeModal} />
        </Modal>
      )}
    </div>
  );
};

export default ProductList;
